import {
  Image,
  Pressable,
  ScrollView,
  StyleSheet,
  Text,
  View,
} from 'react-native';
import React, { useLayoutEffect } from 'react';
import { AntDesign, Feather } from '@expo/vector-icons';
import { useDispatch, useSelector } from 'react-redux';

import {
  decrementQuantity,
  incrementQuantity,
  removeFromCart,
} from '../redux/CartReducer';
import LottieView from 'lottie-react-native';

import { useNavigation } from '@react-navigation/native';

const CartScreen = () => {
  const navigation = useNavigation(); // Moved up for better context

  useLayoutEffect(() => {
    navigation.setOptions({
      headerTitle: () => (
        <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center', width: 350, height: 20 }}>
          <Text style={{ color: 'black', fontWeight: 'bold', fontSize: 19 }}>
            Cart Section
          </Text>
        </View>
      ),
      headerStyle: {
        backgroundColor: 'transparent',
      },
      headerRight: () => (
        <View
          style={{
            flexDirection: 'row',
            alignItems: 'center',
            justifyContent: 'center',
            gap: 6,
            marginRight: 12,
            fontSize: 13,
          }}
        >
          {/* Add any buttons or icons here */}
        </View>
      ),
    });
  }, [navigation]);

  const cart = useSelector((state) => state.cart.cart);
  const total = cart?.map((product) => product.price * product.quantity)
    .reduce((curr, prev) => curr + prev, 0) || 0;

  const dispatch = useDispatch();

  const increaseQuantity = (product) => {
    dispatch(incrementQuantity(product));
  };

  const decreaseQuantity = (product) => {
    dispatch(decrementQuantity(product));
  };

  const deleteItem = (product) => {
    dispatch(removeFromCart(product));
  };

  return (
    <ScrollView
      showsVerticalScrollIndicator={false}
      showsHorizontalScrollIndicator={false}
      style={{ marginTop: 0, flex: 1, backgroundColor: 'white' }}
    >
      {cart.length > 0 ? <>
        <View style={{ padding: 10, flexDirection: 'row', alignItems: 'center' }}>
          <Text style={{ fontSize: 18, fontWeight: '400' }}>Subtotal: </Text>
          <Text style={{ fontSize: 20, fontWeight: 'bold' }}>â‚¹{total}</Text>
        </View>

        <Pressable
          onPress={() => navigation.navigate('Confirm')}
          style={{
            backgroundColor: '#FFC72C',
            padding: 10,
            borderRadius: 5,
            justifyContent: 'center',
            alignItems: 'center',
            marginHorizontal: 10,
            marginTop: 10,
          }}
        >
          <Text style={{fontWeight:'bold'}}>Proceed to Buy ({cart.length}) product</Text>
        </Pressable></>
        :
        <>
          <Text style={{ textAlign: 'center', position: 'absolute', top: 90, left: 90, fontSize: 20, fontWeight: 'bold', color: 'gray' }}>No products in the Cart ðŸ˜”</Text>
          <LottieView
            source={require('../assets/emptycart.json')}
            style={{
              height: 280,
              width: 320,
              alignSelf: 'center',
              marginTop: 150,
              justifyContent: 'center',
            }}
            autoPlay
            loop={true}
            speed={0.7}
          />
        </>

      }



      <View style={{ marginHorizontal: 10 }}>
        {cart?.map((product, index) => (
          <View 
            key={index}
            style={{
              backgroundColor: 'white',
              marginVertical: 10,
              borderBottomColor: '#F0F0F0',
              borderWidth: 2,
              borderLeftWidth: 0,
              borderRightWidth: 0,
              borderTopWidth: 0,
              borderColor: 'white',
              borderRadius:6,
             borderWidth: 2,
             shadowColor: 'black',
             shadowOffset: { width: 0, height: 6 },
             shadowRadius: 15,
             elevation: 10,backgroundColor:'white'
            }}
          >
            <Pressable
              style={{
                marginVertical: 10,
                flexDirection: 'row',
                justifyContent: 'space-between', 
               
              }}
            >
              <View >
                <Image
                  style={{ width: 140, height: 140, resizeMode: 'contain' }}
                  source={{ uri: product?.image ? product?.image :product?.imageurl  }}
                />
              </View >
              <View >
                <Text numberOfLines={3} style={{ width: 150, marginTop: 10, fontSize: 15, fontWeight: 'bold', color: '#0a3d62' }}>
                  {product?.title ? product?.title : product?.name}
                </Text>
                <Text style={{ fontSize: 15, fontWeight: 'bold', marginTop: 6, color: '#0a3d62' }}>
                  Price : â‚¹ {product?.price}
                </Text>
                <Text style={{ fontSize: 14, fontWeight: 'bold', marginTop: 6, color: '#0a3d62' }}>
                 Total Price: â‚¹ {product?.price} x {product?.quantity} = â‚¹ {product?.price*product?.quantity}
                </Text>
                <Pressable
                  style={{
                    marginTop: 15,
                    marginBottom: 10,
                    flexDirection: 'row',
                    alignItems: 'center',
                    gap: 10,
                  }}
                >
                  <View
                    style={{
                      flexDirection: 'row',
                      alignItems: 'center',
                      paddingHorizontal: 10,
                      paddingVertical: 5,
                      borderRadius: 7,
                    }}
                  >
                    {product?.quantity > 1 ? (
                      <Pressable
                        onPress={() => decreaseQuantity(product)}
                        style={{
                          backgroundColor: '#eb3b5a',
                          padding: 7,
                          borderTopLeftRadius: 6,
                          borderBottomLeftRadius: 6,
                        }}
                      >
                        <AntDesign name="minus" size={24} color="#fff" />
                      </Pressable>
                    ) : (
                      <Pressable
                        onPress={() => deleteItem(product)}
                        style={{
                          backgroundColor: '#eb3b5a',
                          padding: 7,
                          borderTopLeftRadius: 6,
                          borderBottomLeftRadius: 6,
                        }}
                      >
                        <AntDesign name="delete" size={24} color="#fff" />
                      </Pressable>
                    )}

                    <Pressable>
                      <Text
                        style={{
                          backgroundColor: 'white',
                          paddingHorizontal: 18,
                          paddingVertical: 8,fontWeight:'bold'
                        }}
                      >
                        {product?.quantity}
                      </Text>
                    </Pressable>
                    <Pressable
                      onPress={() => increaseQuantity(product)}
                      style={{
                        backgroundColor: '#20bf6b',
                        padding: 7,
                        borderTopRightRadius: 6,
                        borderBottomRightRadius: 6,
                      }}
                    >
                      <Feather name="plus" size={24} color="#fff" />
                    </Pressable>
                  </View>
                  <Pressable
                    onPress={() => deleteItem(product)}
                    style={{
                      backgroundColor: '#fa8231',
                      paddingHorizontal: 8,
                      paddingVertical: 10,
                      borderRadius: 5,
                      borderColor: '#0a3d62',
                      borderWidth: 0.6,marginRight:6
                    }}
                  >
                    <Text style={{ color: '#fff' }}>Delete</Text>
                  </Pressable>
                </Pressable>
              </View>
            </Pressable>
          </View>
        ))}
      </View>
    </ScrollView>
  );
};

export default CartScreen;

const styles = StyleSheet.create({});
